reconcile.Result
reconcileNamespaces

tenant_controller.go

func (r *TenantReconciler) reconcileNamespaces(ctx context.Context, log logr.Logger, tenant multitenancyv1.Tenant) (bool, error) {
    //! [matching-fields]
    var namespaces corev1.NamespaceList
    err := r.List(ctx, &namespaces, client.MatchingFields(map[string]string{ownerControllerField: tenant.Name}))
    //! [matching-fields]
    if err != nil {
        log.Error(err, "unable to fetch namespaces")
        return false, err
    }
    namespaceNames := make(map[string]corev1.Namespace)
    for _, ns := range namespaces.Items {
        namespaceNames[ns.Name] = ns
    }

    updated := false
    for _, ns := range tenant.Spec.Namespaces {
        name := tenant.Spec.NamespacePrefix + ns
        if _, ok := namespaceNames[name]; ok {
            delete(namespaceNames, name)
            continue
        }
        target := corev1.Namespace{
            ObjectMeta: metav1.ObjectMeta{
                Name: name,
            },
        }
        //! [controller-reference]
        err = ctrl.SetControllerReference(&tenant, &target, r.Scheme)
        //! [controller-reference]
        if err != nil {
            log.Error(err, "unable to set owner reference", "name", name)
            return updated, err
        }
        log.Info("creating the new namespace", "name", name)
        err = r.Create(ctx, &target, &client.CreateOptions{})
        if err != nil {
            log.Error(err, "unable to create the namespace", "name", name)
            return updated, err
        }
        addedNamespaces.Inc()
        updated = true
        delete(namespaceNames, name)
    }

    for _, ns := range namespaceNames {
        log.Info("deleting the new namespace", "name", ns.Name)
        err = r.Delete(ctx, &ns, &client.DeleteOptions{})
        if err != nil {
            log.Error(err, "unable to delete the namespace", "name", ns.Name)
            return updated, err
        }
        removedNamespaces.Inc()
        updated = true
    }

    return updated, nil
}

reconcileRBAC

tenant_controller.go

func (r *TenantReconciler) reconcileRBAC(ctx context.Context, log logr.Logger, tenant multitenancyv1.Tenant) (bool, error) {
    updated := false
    for _, ns := range tenant.Spec.Namespaces {
        //! [create-or-update]
        name := tenant.Spec.NamespacePrefix + ns

        role := &rbacv1.ClusterRole{}
        role.SetName(name + "-admin-role")
        op, err := ctrl.CreateOrUpdate(ctx, r.Client, role, func() error {
            role.Rules = []rbacv1.PolicyRule{
                {
                    Verbs:         []string{"get", "list", "watch", "update", "patch", "delete"},
                    APIGroups:     []string{multitenancyv1.GroupVersion.Group},
                    Resources:     []string{"tenants"},
                    ResourceNames: []string{tenant.Name},
                },
                {
                    Verbs:         []string{"get", "list", "watch"},
                    APIGroups:     []string{""},
                    Resources:     []string{"namespaces"},
                    ResourceNames: []string{name},
                },
            }
            return ctrl.SetControllerReference(&tenant, role, r.Scheme)
        })
        //! [create-or-update]
        if err != nil {
            log.Error(err, "unable to create-or-update RoleBinding")
            return updated, err
        }

        if op != controllerutil.OperationResultNone {
            updated = true
            log.Info("reconcile RoleBinding successfully", "op", op)
        }

        rb := &rbacv1.RoleBinding{}
        rb.SetNamespace(name)
        rb.SetName(name + "-admin-rolebinding")

        op, err = ctrl.CreateOrUpdate(ctx, r.Client, rb, func() error {
            rb.RoleRef = rbacv1.RoleRef{
                APIGroup: "rbac.authorization.k8s.io",
                Kind:     "ClusterRole",
                Name:     name + "-admin-role",
            }
            rb.Subjects = []rbacv1.Subject{tenant.Spec.Admin}
            return ctrl.SetControllerReference(&tenant, rb, r.Scheme)
        })
        if err != nil {
            log.Error(err, "unable to create-or-update RoleBinding")
            return updated, err
        }

        if op != controllerutil.OperationResultNone {
            updated = true
            log.Info("reconcile RoleBinding successfully", "op", op)
        }
    }
    return updated, nil
}

ステータスの更新

tenant_controller.go

func (r *TenantReconciler) reconcile(ctx context.Context, log logr.Logger, tenant multitenancyv1.Tenant) (bool, error) {
    nsUpdated, err := r.reconcileNamespaces(ctx, log, tenant)
    if err != nil {
        return nsUpdated, err
    }
    rbUpdated, err := r.reconcileRBAC(ctx, log, tenant)
    if err != nil {
        return rbUpdated, err
    }
    return nsUpdated || rbUpdated, nil
}


tenant_controller.go

updated, err := r.reconcile(ctx, log, tenant)
if err != nil {
    log.Error(err, "unable to reconcile", "name", tenant.Name)
    r.Recorder.Eventf(&tenant, corev1.EventTypeWarning, "Failed", "failed to reconciled: %s", err.Error())
    setCondition(&tenant.Status.Conditions, multitenancyv1.TenantCondition{
        Type:    multitenancyv1.ConditionReady,
        Status:  corev1.ConditionFalse,
        Reason:  "Failed",
        Message: err.Error(),
    })
    stErr := r.Status().Update(ctx, &tenant)
    if stErr != nil {
        log.Error(stErr, "failed to update status", "name", tenant.Name)
    }
    return ctrl.Result{}, err
}

if updated {
    r.Recorder.Event(&tenant, corev1.EventTypeNormal, "Updated", "the tenant was updated")
    setCondition(&tenant.Status.Conditions, multitenancyv1.TenantCondition{
        Type:   multitenancyv1.ConditionReady,
        Status: corev1.ConditionTrue,
    })
    err = r.Status().Update(ctx, &tenant)
    if err != nil {
        log.Error(err, "failed to update status", "name", tenant.Name)
        return ctrl.Result{}, err
    }
}

controller-runtime/controller_test.html.body:
コントローラのテスト
controller-runtimeではenvtestパッケージを提供しており、コントローラやWebhookの簡易的なテストを実施することが可能です。
envtestはetcdとkube-apiserverを立ち上げてテスト用の環境を構築します。
環境変数USE_EXISTING_CLUSTERを指定すれば、既存のKubernetesクラスタを利用したテストをおこなうことも可能です。
なおcontroller-genが生成するテストコードでは、Ginkgoというテストフレームワークを利用しています。
このフレームワークの利用方法についてはGinkgonoのドキュメントを御覧ください。
テスト環境のセットアップ
controller-genによって自動生成されたcontrollers/suite_test.goを見てみましょう。

controllers/suite_test.go

package controllers

import (
    "path/filepath"
    "testing"

    . "github.com/onsi/ginkgo"
    . "github.com/onsi/gomega"
    multitenancyv1 "github.com/zoetrope/kubebuilder-training/codes/api/v1"
    "k8s.io/client-go/kubernetes/scheme"
    "k8s.io/client-go/rest"
    ctrl "sigs.k8s.io/controller-runtime"
    "sigs.k8s.io/controller-runtime/pkg/client"
    "sigs.k8s.io/controller-runtime/pkg/envtest"
    "sigs.k8s.io/controller-runtime/pkg/envtest/printer"
    logf "sigs.k8s.io/controller-runtime/pkg/log"
    "sigs.k8s.io/controller-runtime/pkg/log/zap"
    // +kubebuilder:scaffold:imports
)

// These tests use Ginkgo (BDD-style Go testing framework). Refer to
// http://onsi.github.io/ginkgo/ to learn more about Ginkgo.

var cfg *rest.Config
var k8sClient client.Client
var testEnv *envtest.Environment

func TestAPIs(t *testing.T) {
    RegisterFailHandler(Fail)

    RunSpecsWithDefaultAndCustomReporters(t,
        "Controller Suite",
        []Reporter{printer.NewlineReporter{}})
}

var _ = BeforeSuite(func(done Done) {
    logf.SetLogger(zap.LoggerTo(GinkgoWriter, true))

    By("bootstrapping test environment")
    testEnv = &envtest.Environment{
        CRDDirectoryPaths: []string{filepath.Join("..", "config", "crd", "bases")},
    }

    var err error
    cfg, err = testEnv.Start()
    Expect(err).ToNot(HaveOccurred())
    Expect(cfg).ToNot(BeNil())

    err = multitenancyv1.AddToScheme(scheme.Scheme)
    Expect(err).NotTo(HaveOccurred())

    // +kubebuilder:scaffold:scheme

    mgr, err := ctrl.NewManager(cfg, ctrl.Options{
        Scheme: scheme.Scheme,
    })
    Expect(err).ToNot(HaveOccurred())

    err = (&TenantReconciler{
        Client:   mgr.GetClient(),
        Log:      ctrl.Log.WithName("controllers").WithName("Tenant"),
        Scheme:   mgr.GetScheme(),
        Recorder: mgr.GetEventRecorderFor("tenant-controller"),
    }).SetupWithManager(mgr)
    Expect(err).ToNot(HaveOccurred())

    go func() {
        err = mgr.Start(ctrl.SetupSignalHandler())
        Expect(err).ToNot(HaveOccurred())
    }()

    k8sClient = mgr.GetClient()
    Expect(k8sClient).ToNot(BeNil())

    close(done)
}, 60)

var _ = AfterSuite(func() {
    By("tearing down the test environment")
    err := testEnv.Stop()
    Expect(err).ToNot(HaveOccurred())
})

まずenvtest.Environmentでテスト用の環境設定をおこないます。
ここでは、CRDDirectoryPathsで適用するCRDのマニフェストのパスを指定しています。
testEnv.Start()を呼び出すとetcdとkube-apiserverが起動します。
あとはコントローラのメイン関数と同様に初期化処理をおこなうだけです。
テスト終了時にはetcdとkube-apiserverを終了するようにtestEnv.Stop()を呼び出します。
コントローラのテスト
それでは実際のテストを書いていきましょう。

controllers/tenant_controller_test.go

package controllers

import (
    "context"
    "errors"

    . "github.com/onsi/ginkgo"
    . "github.com/onsi/gomega"
    multitenancyv1 "github.com/zoetrope/kubebuilder-training/codes/api/v1"
    corev1 "k8s.io/api/core/v1"
    rbacv1 "k8s.io/api/rbac/v1"
    metav1 "k8s.io/apimachinery/pkg/apis/meta/v1"
    "k8s.io/apimachinery/pkg/types"
    "sigs.k8s.io/controller-runtime/pkg/client"
)

var _ = Describe("Tenant controller", func() {

    const (
        tenantName = "sample"
    )

    Context("when creating Tenant resource", func() {
        It("Should create namespaces", func() {
            ctx := context.Background()
            tenant := &multitenancyv1.Tenant{
                TypeMeta: metav1.TypeMeta{
                    APIVersion: multitenancyv1.GroupVersion.String(),
                    Kind:       "Tenant",
                },
                ObjectMeta: metav1.ObjectMeta{
                    Name: tenantName,
                },
                Spec: multitenancyv1.TenantSpec{
                    Namespaces: []string{
                        "test1",
                        "test2",
                    },
                    NamespacePrefix: "",
                    Admin: rbacv1.Subject{
                        Kind:      "ServiceAccount",
                        Name:      "default",
                        Namespace: "default",
                    },
                },
            }
            err := k8sClient.Create(ctx, tenant)
            Expect(err).Should(Succeed())

            createdTenant := &multitenancyv1.Tenant{}
            Eventually(func() error {
                err := k8sClient.Get(ctx, types.NamespacedName{Name: tenantName}, createdTenant)
                if err != nil {
                    return err
                }
                cond := findCondition(createdTenant.Status.Conditions, multitenancyv1.ConditionReady)
                if cond == nil {
                    return errors.New("condition not found")
                }
                if cond.Status != corev1.ConditionTrue {
                    return errors.New(cond.Reason)
                }
                return nil
            }).Should(Succeed())

            nsList := &corev1.NamespaceList{}
            err = k8sClient.List(ctx, nsList, client.MatchingFields(map[string]string{ownerControllerField: tenant.Name}))
            Expect(err).Should(Succeed())

            var namespaces []string
            for _, ns := range nsList.Items {
                namespaces = append(namespaces, ns.Name)
            }
            Expect(namespaces).Should(ContainElement("test1"))
            Expect(namespaces).Should(ContainElement("test2"))

        })
    })
})

